import React from "react";
import styled from "styled-components";

export const StyledButtonText = styled.p`
  &&& {
    font-family: Futura;
    font-style: normal;
    font-weight: 500;
    font-size: 16px;
    line-height: 21px;

    color: #fcfcfc;
  }
`;
export const StyledStatement = styled.p`
  &&& {
    color: #fcfcfc;
    font-family: Avenir;
    font-size: 32px;
    font-style: normal;
    font-weight: 400;
    line-height: 44px;
    letter-spacing: 0em;
    text-align: left;
  }
`;
export const StyledMissionTitle = styled.h1`
  &&& {
    -webkit-text-stroke: 2px #f9d667; /* width and color */
    color: #fcfcfc;
    font-family: Futura;
    font-size: 150px;
    font-style: normal;
    font-weight: 700;
    line-height: 199px;
    letter-spacing: 0em;
    text-align: left;
    text-shadow: 4px 4px 25px rgba(249, 214, 103, 0.5);
    margin-bottom: 32px;
    margin-top: 48px;
  }
`;
