import styled from "styled-components";

export const StyledTitle = styled.h1`
  &&& {
    /* Title */

    font-family: Futura;
    font-style: normal;
    font-weight: bold;
    font-size: 48px;
    line-height: 64px;
    /* identical to box height */

    color: #ffffff;
  }
`;

export const StyledQuestion = styled.h1`
  &&& {
    /* Heading 2 */

    font-family: Futura;
    font-style: normal;
    font-weight: 500;
    font-size: 24px;
    line-height: 32px;
    /* identical to box height */

    color: #ffffff;
  }
`;

export const StyledAnswer = styled.h1`
  &&& {
    font-family: Avenir;
    font-style: normal;
    font-weight: normal;
    font-size: 16px;
    line-height: 22px;

    /* Light Grey */

    color: #afafaf;
  }
`;

export const StyledDescription = styled.h1`
  &&& {
    font-family: Avenir;
    font-style: normal;
    font-weight: normal;
    font-size: 18px;
    line-height: 25px;
    text-decoration: none;
    color: #ffffff;
  }
`;
